import math

try:
    result = numerical_derivative(lambda x: x**2, 3)
    assert abs(result - 6.0) < 1e-4
    print("PASS: derivative of x^2 at x=3 is ~6")
except:
    print("FAIL: derivative of x^2 at x=3 is ~6")

try:
    result = numerical_derivative(lambda x: x**3, 2)
    assert abs(result - 12.0) < 1e-4
    print("PASS: derivative of x^3 at x=2 is ~12")
except:
    print("FAIL: derivative of x^3 at x=2 is ~12")

try:
    result = numerical_derivative(math.sin, 0)
    assert abs(result - 1.0) < 1e-4
    print("PASS: derivative of sin(x) at x=0 is ~1")
except:
    print("FAIL: derivative of sin(x) at x=0 is ~1")
