try:
    result = derivative_table(lambda x: x**2, [1, 2, 3])
    assert len(result) == 3
    print("PASS: returns correct number of tuples")
except Exception as e:
    print(f"FAIL: returns correct number of tuples — {e}")

try:
    result = derivative_table(lambda x: x**2, [1, 2, 3])
    expected = [2.0, 4.0, 6.0]
    for (x, dfdx), exp in zip(result, expected):
        assert abs(dfdx - exp) < 1e-4
    print("PASS: derivatives of x^2 at [1,2,3] are [2,4,6]")
except Exception as e:
    print(f"FAIL: derivatives of x^2 at [1,2,3] are [2,4,6] — {e}")

try:
    result = derivative_table(lambda x: x**2, [1, 2, 3])
    assert result[0][0] == 1 and result[1][0] == 2 and result[2][0] == 3
    print("PASS: x values are preserved in tuples")
except Exception as e:
    print(f"FAIL: x values are preserved in tuples — {e}")
