solutionCode:
def partial_derivatives(a, b, c, h=1e-5):
    def f(a, b, c):
        return a * b + c
    da = (f(a + h, b, c) - f(a - h, b, c)) / (2 * h)
    db = (f(a, b + h, c) - f(a, b - h, c)) / (2 * h)
    dc = (f(a, b, c + h) - f(a, b, c - h)) / (2 * h)
    return {'da': da, 'db': db, 'dc': dc}

testCode:
try:
    result = partial_derivatives(2, 3, 4)
    assert abs(result['da'] - 3.0) < 1e-4
    print("PASS: df/da at (2,3,4) is ~3 (equal to b)")
except:
    print("FAIL: df/da at (2,3,4) is ~3 (equal to b)")

try:
    result = partial_derivatives(2, 3, 4)
    assert abs(result['db'] - 2.0) < 1e-4
    print("PASS: df/db at (2,3,4) is ~2 (equal to a)")
except:
    print("FAIL: df/db at (2,3,4) is ~2 (equal to a)")

try:
    result = partial_derivatives(2, 3, 4)
    assert abs(result['dc'] - 1.0) < 1e-4
    print("PASS: df/dc at (2,3,4) is ~1")
except:
    print("FAIL: df/dc at (2,3,4) is ~1")
