solutionCode:
class Value:
    def __init__(self, data, _children=(), _op=''):
        self.data = data
        self.grad = 0.0
        self._children = set(_children)
        self._op = _op
        self._backward = lambda: None

    def __repr__(self):
        return f'Value(data={self.data:.4f})'

testCode:
try:
    v = Value(3.0)
    assert v.data == 3.0
    print("PASS: Value(3.0).data == 3.0")
except Exception as e:
    print(f"FAIL: Value(3.0).data == 3.0 — {e}")

try:
    v = Value(3.0)
    assert v.grad == 0.0
    print("PASS: Value(3.0).grad == 0.0")
except Exception as e:
    print(f"FAIL: Value(3.0).grad == 0.0 — {e}")

try:
    v = Value(3.0)
    assert v._children == set()
    print("PASS: Value(3.0)._children is empty set")
except Exception as e:
    print(f"FAIL: Value(3.0)._children is empty set — {e}")

try:
    v = Value(3.0)
    assert v._op == ''
    print("PASS: Value(3.0)._op is empty string")
except Exception as e:
    print(f"FAIL: Value(3.0)._op is empty string — {e}")

try:
    v = Value(3.14)
    assert repr(v) == 'Value(data=3.1400)'
    print("PASS: repr(Value(3.14)) == 'Value(data=3.1400)'")
except Exception as e:
    print(f"FAIL: repr(Value(3.14)) == 'Value(data=3.1400)' — {e}")
