class Value:
    def __init__(self, data, _children=(), _op=''):
        self.data = data
        self.grad = 0.0
        self._backward = lambda: None
        self._prev = set(_children)
        self._op = _op

    def __repr__(self):
        return f"Value(data={self.data}, grad={self.grad})"

    def __add__(self, other):
        other = other if isinstance(other, Value) else Value(other)
        out = Value(self.data + other.data, (self, other), '+')
        def _backward():
            self.grad += out.grad
            other.grad += out.grad
        out._backward = _backward
        return out

    def __mul__(self, other):
        other = other if isinstance(other, Value) else Value(other)
        out = Value(self.data * other.data, (self, other), '*')
        def _backward():
            self.grad += other.data * out.grad
            other.grad += self.data * out.grad
        out._backward = _backward
        return out

    def __pow__(self, other):
        assert isinstance(other, (int, float)), "only supporting int/float powers for now"
        out = Value(self.data ** other, (self,), f'**{other}')
        def _backward():
            self.grad += (other * self.data ** (other - 1)) * out.grad
        out._backward = _backward
        return out

    def relu(self):
        out = Value(0 if self.data < 0 else self.data, (self,), 'relu')
        def _backward():
            self.grad += (out.data > 0) * out.grad
        out._backward = _backward
        return out

    def backward(self):
        topo = []
        visited = set()
        def build_topo(v):
            if v not in visited:
                visited.add(v)
                for child in v._prev:
                    build_topo(child)
                topo.append(v)
        build_topo(self)
        self.grad = 1.0
        for v in reversed(topo):
            v._backward()

    def __radd__(self, other):
        return self + other

    def __rmul__(self, other):
        return self * other

    def __neg__(self):
        return self * -1

    def __sub__(self, other):
        return self + (-other)

    def __truediv__(self, other):
        return self * other**-1


def check_gradients(f, inputs, h=1e-5):
    # Step 1: Compute analytical gradients
    values = [Value(x) for x in inputs]
    out = f(values)
    out.backward()
    analytical_grads = [v.grad for v in values]

    # Step 2: Compute numerical gradients for each input
    numerical_grads = []
    for i in range(len(inputs)):
        # Nudge input i by +h
        inputs_plus = list(inputs)
        inputs_plus[i] += h
        values_plus = [Value(x) for x in inputs_plus]
        f_plus = f(values_plus)

        # Nudge input i by -h
        inputs_minus = list(inputs)
        inputs_minus[i] -= h
        values_minus = [Value(x) for x in inputs_minus]
        f_minus = f(values_minus)

        numerical_grads.append((f_plus.data - f_minus.data) / (2 * h))

    # Step 3: Compare
    for a, n in zip(analytical_grads, numerical_grads):
        if abs(a - n) > 1e-4:
            return False
    return True

