try:
    def f_mul(vals):
        return vals[0] * vals[1]
    result = check_gradients(f_mul, [3.0, 4.0])
    assert result == True, "Gradient check failed for f(a,b) = a*b"
    print("PASS: gradient check for f(a,b) = a*b")
except:
    print("FAIL: gradient check for f(a,b) = a*b")

try:
    def f_poly(vals):
        return vals[0] ** 2 + vals[1]
    result = check_gradients(f_poly, [3.0, 4.0])
    assert result == True, "Gradient check failed for f(a,b) = a^2 + b"
    print("PASS: gradient check for f(a,b) = a^2 + b")
except:
    print("FAIL: gradient check for f(a,b) = a^2 + b")

try:
    def f_relu_pos(vals):
        return vals[0].relu()
    result = check_gradients(f_relu_pos, [3.0])
    assert result == True, "Gradient check failed for relu(positive)"
    print("PASS: gradient check for relu(positive)")
except:
    print("FAIL: gradient check for relu(positive)")

try:
    def f_relu_neg(vals):
        return vals[0].relu()
    result = check_gradients(f_relu_neg, [-3.0])
    assert result == True, "Gradient check failed for relu(negative)"
    print("PASS: gradient check for relu(negative)")
except:
    print("FAIL: gradient check for relu(negative)")

try:
    def f_complex(vals):
        return (vals[0] * vals[1] + vals[2] ** 2).relu()
    result = check_gradients(f_complex, [2.0, 3.0, 1.0])
    assert result == True, "Gradient check failed for complex expression"
    print("PASS: gradient check for complex expression")
except:
    print("FAIL: gradient check for complex expression")
